class Solution {
    fun goodSubsetofBinaryMatrix(grid: Array<IntArray>): List<Int> {
        val m = grid.size
        val n = grid[0].size

        for (mask in 1 until (1 shl m)) {
            val subset = mutableListOf<Int>()
            val columnSums = IntArray(n)
            var rowCount = 0

            for (row in 0 until m) {
                if (mask and (1 shl row) != 0) {
                    rowCount++
                    subset.add(row)
                    for (col in 0 until n) {
                        columnSums[col] += grid[row][col]
                    }
                }
            }

            var isValidSubset = true
            for (sum in columnSums) {
                if (sum > rowCount / 2) {
                    isValidSubset = false
                    break
                }
            }

            if (isValidSubset) return subset
        }

        return emptyList()
    }
}
