object Solution {
    def goodSubsetofBinaryMatrix(grid: Array[Array[Int]]): List[Int] = {
        val m = grid.length
        val n = grid(0).length
        val allRows = (0 until m).toList

        def isValidSubset(rows: List[Int]): Boolean = {
            (0 until n).forall { col =>
                rows.map(grid(_)(col)).sum <= rows.length / 2
            }
        }

        def findSubset(rows: List[Int], index: Int): List[Int] = {
            if (index == m) {
                if (isValidSubset(rows)) rows else Nil
            } else {
                val withCurrent = findSubset(index :: rows, index + 1)
                if (withCurrent.nonEmpty) withCurrent
                else findSubset(rows, index + 1)
            }
        }

        findSubset(Nil, 0)
    }
}
