#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    vector<int> leftmostBuildingQueries(vector<int>& heights, vector<vector<int>>& queries) {
        int n = heights.size();
        vector<int> maxRight(n);
        maxRight[n - 1] = heights[n - 1];
        for (int i = n - 2; i >= 0; --i) {
            maxRight[i] = max(maxRight[i + 1], heights[i]);
        }

        vector<int> ans;
        for (const auto& q : queries) {
            int a = q[0], b = q[1];
            if (heights[a] >= maxRight[b] || a >= b) {
                ans.push_back(-1);
                continue;
            }

            int left = a + 1, right = b, meet = -1;
            while (left <= right) {
                int mid = left + (right - left) / 2;
                if (heights[mid] > heights[a] && heights[mid] > heights[b]) {
                    meet = mid;
                    right = mid - 1;
                } else {
                    left = mid + 1;
                }
            }
            ans.push_back(meet);
        }

        return ans;
    }
};
