#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    vector<int> leftmostBuildingQueries(vector<int>& heights, vector<vector<int>>& queries) {
        int n = heights.size();
        vector<int> ans(queries.size(), -1);
        for (int i = 0; i < queries.size(); ++i) {
            int a = queries[i][0], b = queries[i][1];
            if (a == b) {
                ans[i] = a;
                continue;
            }
            if (a > b) swap(a, b);
            int max_height = heights[a];
            for (int j = a + 1; j <= b; ++j) {
                if (heights[j] > max_height) {
                    max_height = heights[j];
                    ans[i] = j;
                    if (j > a && j < b) break; // Optimize by breaking early if a meeting point is found
                }
            }
        }
        return ans;
    }
};
