#include <stdlib.h>

int* leftmostBuildingQueries(int* heights, int heightsSize, int** queries, int queriesSize, int* queriesColSize, int* returnSize) {
    *returnSize = queriesSize;
    int* ans = (int*)malloc(queriesSize * sizeof(int));
    for (int i = 0; i < queriesSize; ++i) {
        int a = queries[i][0], b = queries[i][1];
        if (a == b) {
            ans[i] = a; // Alice and Bob are in the same building
            continue;
        }
        int direction = a < b ? 1 : -1; // Determine search direction
        int meet = -1, maxHeight = heights[a];
        for (int j = a + direction; j != b + direction; j += direction) {
            if (heights[j] > maxHeight) {
                maxHeight = heights[j];
                if (j > b) { // Found a meeting point beyond b
                    meet = j;
                    break;
                }
            }
        }
        if (meet == -1 && maxHeight > heights[b]) meet = b;
        ans[i] = (meet > b || meet < 0) ? -1 : meet;
    }
    return ans;
}
