class Solution {
    public int[] leftmostBuildingQueries(int[] heights, int[][] queries) {
        int n = heights.length;
        int[] ans = new int[queries.length];
        int[][] maxRight = new int[n][2]; // [0]: max height till now, [1]: index of max height
        maxRight[n - 1][0] = heights[n - 1];
        maxRight[n - 1][1] = n - 1;
        for (int i = n - 2; i >= 0; i--) {
            if (heights[i] > maxRight[i + 1][0]) {
                maxRight[i][0] = heights[i];
                maxRight[i][1] = i;
            } else {
                maxRight[i][0] = maxRight[i + 1][0];
                maxRight[i][1] = maxRight[i + 1][1];
            }
        }

        for (int i = 0; i < queries.length; i++) {
            int a = queries[i][0], b = queries[i][1];
            if (heights[a] >= maxRight[b][0]) {
                ans[i] = -1;
                continue;
            }

            int left = a, right = b;
            while (left < right) {
                int mid = left + (right - left) / 2;
                if (maxRight[mid][0] > heights[a] && maxRight[mid][0] > heights[b]) {
                    right = mid;
                } else {
                    left = mid + 1;
                }
            }
            ans[i] = (heights[left] > heights[a] && heights[left] > heights[b]) ? left : -1;
        }
        return ans;
    }
}
