from typing import List

class Solution:
    def leftmostBuildingQueries(self, heights: List[int], queries: List[List[int]]) -> List[int]:
        n = len(heights)
        max_right = [-1] * n  # max_right[i] stores the index of the first building taller than the ith building to its right
        stack = []
        for i in range(n - 1, -1, -1):
            while stack and heights[i] >= heights[stack[-1]]:
                stack.pop()
            if stack:
                max_right[i] = stack[-1]
            stack.append(i)

        ans = []
        for a, b in queries:
            if a >= b:
                ans.append(-1)  # Alice cannot move left or stay in the same building
            else:
                current = a
                while current != -1 and current < b:
                    current = max_right[current]
                    if current != -1 and current <= b:
                        ans.append(current)
                        break
                else:
                    ans.append(-1)  # Alice cannot reach a building where she can meet Bob
        return ans
