object Solution {
    def leftmostBuildingQueries(heights: Array[Int], queries: Array[Array[Int]]): Array[Int] = {
        val n = heights.length
        val maxRight = Array.fill(n)(-1)
        var stack = List[Int]()

        for (i <- heights.indices.reverse) {
            while (stack.nonEmpty && heights(i) >= heights(stack.head)) {
                stack = stack.tail
            }
            if (stack.nonEmpty) maxRight(i) = stack.head
            stack = i :: stack
        }

        queries.map { case Array(a, b) =>
            if (a >= b) -1
            else {
                var i = a
                var canMeet = false
                while (i != -1 && i <= b) {
                    if (i == b) {
                        canMeet = true
                        i = -1
                    } else i = maxRight(i)
                }
                if (canMeet) b else -1
            }
        }
    }
}
