class Solution {
    func leftmostBuildingQueries(_ heights: [Int], _ queries: [[Int]]) -> [Int] {
        var ans = [Int]()

        for query in queries {
            let a = query[0], b = query[1]
            if a == b {
                ans.append(a)
                continue
            }

            let direction = a < b ? 1 : -1
            var meetPoint = -1
            var maxLeft = heights[a], maxRight = heights[b]
            var i = a, j = b

            while i != j {
                if direction > 0 { // Moving right
                    i += direction
                    if heights[i] > maxLeft {
                        maxLeft = heights[i]
                        if maxLeft >= maxRight {
                            meetPoint = i
                            break
                        }
                    }
                } else { // Moving left
                    j += direction
                    if heights[j] > maxRight {
                        maxRight = heights[j]
                        if maxRight >= maxLeft {
                            meetPoint = j
                            break
                        }
                    }
                }
            }

            // Check if they can meet at the current building
            if heights[i] >= maxLeft && heights[j] >= maxRight {
                meetPoint = direction > 0 ? i : j
            }

            ans.append(meetPoint)
        }

        return ans
    }
}
