public class Solution {
    public int FindMaximumLength(int[] nums) {
        int n = nums.Length;
        if (n == 1) return 1; // If the array has only one element, the maximum length of a non-decreasing array is 1.

        int maxLength = 1; // The minimum non-decreasing array length is 1.
        for (int i = 0; i < n; i++) {
            int sum = nums[i];
            for (int j = i + 1; j < n; j++) {
                sum += nums[j];
                if (sum >= nums[j] * (j - i + 1)) { // Check if replacing the subarray [i, j] with its sum keeps the array non-decreasing.
                    maxLength = Math.Max(maxLength, j - i + 2); // Update maxLength to include the current subarray and the next element.
                } else {
                    break; // If the condition is not met, break the inner loop.
                }
            }
        }
        return maxLength; // Return the maximum length of a non-decreasing array after applying operations.
    }
}
