#include <vector>
using namespace std;

class Solution {
public:
    int findMaximumLength(vector<int>& nums) {
        // Since the question asks for the maximum length of a non-decreasing array that can be made,
        // and we can replace any subarray by its sum, the only case when we cannot form a non-decreasing
        // array of length more than 1 is when all elements are equal. In any other case, we can always
        // perform operations to make the array non-decreasing of length at least 2.
        // So, we check if all elements are equal, return 1, else return 2.

        bool allEqual = true;
        for (int i = 1; i < nums.size() && allEqual; ++i) {
            if (nums[i] != nums[i-1]) {
                allEqual = false;
            }
        }

        // If all elements are equal, the only non-decreasing array we can get after operations is of length 1.
        // Otherwise, we can always find a subarray to perform operation and make the array non-decreasing of
        // length at least 2.
        return allEqual ? 1 : 2;
    }
};
