#include <stdio.h>

int findMaximumLength(int* nums, int numsSize) {
    if (numsSize == 1) return 1;
    int dp[numsSize];
    int maxLen = 0;

    for (int i = 0; i < numsSize; ++i) dp[i] = 1;

    for (int i = 1; i < numsSize; ++i) {
        for (int j = 0; j < i; ++j) {
            if (nums[i] >= nums[j]) {
                dp[i] = (dp[j] + 1 > dp[i]) ? dp[j] + 1 : dp[i];
            }
        }
        maxLen = (dp[i] > maxLen) ? dp[i] : maxLen;
    }

    // Find the longest sequence of non-decreasing elements
    int longestNonDecreasing = 1, currentLength = 1;
    for (int i = 1; i < numsSize; ++i) {
        if (nums[i - 1] <= nums[i]) {
            currentLength++;
            longestNonDecreasing = (currentLength > longestNonDecreasing) ? currentLength : longestNonDecreasing;
        } else {
            currentLength = 1;
        }
    }

    return longestNonDecreasing > maxLen ? longestNonDecreasing : 1;
}
