class Solution {
  int findMaximumLength(List<int> nums) {
    int n = nums.length;
    List<int> left = List.filled(n, 0);
    List<int> right = List.filled(n, 0);
    left[0] = 1;
    for (int i = 1; i < n; i++) {
      if (nums[i] >= nums[i - 1]) {
        left[i] = left[i - 1] + 1;
      } else {
        left[i] = 1;
      }
    }
    right[n - 1] = 1;
    for (int i = n - 2; i >= 0; i--) {
      if (nums[i] <= nums[i + 1]) {
        right[i] = right[i + 1] + 1;
      } else {
        right[i] = 1;
      }
    }
    int maxLength = 0;
    for (int i = 0; i < n; i++) {
      if (i > 0 && i < n - 1 && nums[i - 1] <= nums[i + 1]) {
        maxLength = max(maxLength, left[i - 1] + right[i + 1]);
      } else {
        maxLength = max(maxLength, max(left[i], right[i]));
      }
    }
    return maxLength;
  }

  int max(int a, int b) => a > b ? a : b;
}
