func findMaximumLength(nums []int) int {
    n := len(nums)
    if n == 1 {
        return 1
    }

    // dp[i] represents the maximum length of a non-decreasing subarray ending at index i
    dp := make([]int, n)
    for i := range dp {
        dp[i] = 1 // Each element by itself is a non-decreasing subarray
    }

    maxLen := 1
    for i := 1; i < n; i++ {
        if nums[i] >= nums[i-1] {
            dp[i] = dp[i-1] + 1
            if dp[i] > maxLen {
                maxLen = dp[i]
            }
        }
    }

    // If the entire array is non-decreasing, return its length
    if maxLen == n {
        return n
    }

    // Otherwise, the maximum length of a non-decreasing array can be made is at least 2
    // because any two adjacent elements can be replaced with their sum
    return max(2, maxLen)
}

// Helper function to find maximum of two integers
func max(a, b int) int {
    if a > b {
        return a
    }
    return b
}
