class Solution {
    public int findMaximumLength(int[] nums) {
        // Initialize maximum length to 1 since any single element is non-decreasing
        int maxLength = 1;

        // Iterate through the array to find lengths of non-decreasing subarrays
        for (int i = 0; i < nums.length; i++) {
            int count = 1; // Start with count 1 for the current element

            // Check for non-decreasing order from the current element
            for (int j = i + 1; j < nums.length; j++) {
                if (nums[j] >= nums[j - 1]) {
                    count++;
                } else {
                    break; // Break if the sequence becomes decreasing
                }
            }

            // Update maxLength if a longer non-decreasing subarray is found
            maxLength = Math.max(maxLength, count);
        }

        // Check if the entire array can be made non-decreasing by a single operation
        // This is the case if there is at most one decreasing pair
        int decreasingPairs = 0;
        for (int i = 1; i < nums.length && decreasingPairs <= 1; i++) {
            if (nums[i] < nums[i - 1]) {
                decreasingPairs++;
            }
        }

        // If the array is already non-decreasing or can be made non-decreasing by one operation
        if (decreasingPairs <= 1) {
            return nums.length; // The entire array length is the maximum non-decreasing length
        }

        // If more than one operation is needed, the maximum length found is returned
        return maxLength;
    }
}
