/**
 * @param {number[]} nums
 * @return {number}
 */
var findMaximumLength = function(nums) {
    let left = 0, right = nums.length - 1;
    while (left < nums.length - 1 && nums[left] <= nums[left + 1]) {
        left++;
    }
    while (right > 0 && nums[right] >= nums[right - 1]) {
        right--;
    }
    if (left >= right) return nums.length;
    let result = Math.max(left + 1, nums.length - right);
    for (let i = 0; i <= left; i++) {
        while (right < nums.length && nums[i] > nums[right]) {
            right++;
        }
        if (right < nums.length) {
            result = Math.max(result, right - i);
        }
    }
    return result;
};
