class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function findMaximumLength($nums) {
        $length = count($nums);
        $left = array_fill(0, $length, 1);
        $right = array_fill(0, $length, 1);

        for ($i = 1; $i < $length; $i++) {
            if ($nums[$i] > $nums[$i - 1]) {
                $left[$i] = $left[$i - 1] + 1;
            }
        }

        for ($i = $length - 2; $i >= 0; $i--) {
            if ($nums[$i] < $nums[$i + 1]) {
                $right[$i] = $right[$i + 1] + 1;
            }
        }

        $maxLen = 1;
        for ($i = 0; $i < $length; $i++) {
            if ($i > 0) {
                $maxLen = max($maxLen, $left[$i - 1] + 1);
            }
            if ($i < $length - 1) {
                $maxLen = max($maxLen, $right[$i + 1] + 1);
            }
            if ($i > 0 && $i < $length - 1 && $nums[$i - 1] < $nums[$i + 1]) {
                $maxLen = max($maxLen, $left[$i - 1] + $right[$i + 1] + 1);
            }
        }

        return $maxLen;
    }
}
