class Solution:
    def findMaximumLength(self, nums: List[int]) -> int:
        n = len(nums)
        if n == 1:
            return 1
        # Initialize dp array where dp[i] stores the maximum length of a non-decreasing subarray ending at i
        dp = [1] * n
        for i in range(1, n):
            if nums[i] >= nums[i - 1]:
                dp[i] = dp[i - 1] + 1
        # Find the maximum length of increasing subarray
        max_len = max(dp)
        # Check if the whole array can be made non-decreasing by one operation
        for i in range(n):
            for j in range(i + 1, n + 1):
                temp = nums[:i] + [sum(nums[i:j])] + nums[j:]
                for k in range(1, len(temp)):
                    if temp[k] < temp[k - 1]:
                        break
                else:  # The temp array is non-decreasing
                    return n
        return max_len
