impl Solution {
    pub fn find_maximum_length(nums: Vec<i32>) -> i32 {
        let mut max_len = 1;
        let mut increasing = vec![1; nums.len()];

        for i in 1..nums.len() {
            if nums[i] >= nums[i - 1] {
                increasing[i] = increasing[i - 1] + 1;
                max_len = max_len.max(increasing[i]);
            }
        }

        for i in 0..nums.len() {
            if i > 0 && nums[i] >= nums[i - 1] {
                continue;
            }
            if i + 1 < nums.len() && nums[i] >= nums[i + 1] {
                continue;
            }
            let left = if i == 0 { 0 } else { increasing[i - 1] };
            let right = if i == nums.len() - 1 { 0 } else {
                let mut max_right = 1;
                let mut current = nums[i + 1];
                for j in i + 2..nums.len() {
                    if nums[j] >= current {
                        max_right += 1;
                        current = nums[j];
                    } else {
                        break;
                    }
                }
                max_right
            };
            max_len = max_len.max(left + right);
        }

        max_len
    }
}
