object Solution {
    def canTraverseAllPairs(nums: Array[Int]): Boolean = {
        val maxVal = 100000
        val parent = Array.tabulate(maxVal + 1)(identity)
        val primeParent = Array.fill(maxVal + 1)(-1)

        def find(x: Int): Int = if (parent(x) == x) x else {
            parent(x) = find(parent(x))
            parent(x)
        }

        def union(x: Int, y: Int): Unit = {
            val rootX = find(x)
            val rootY = find(y)
            if (rootX != rootY) {
                parent(rootX) = rootY
            }
        }

        nums.foreach { num =>
            var x = num
            for (i <- 2 to math.sqrt(num).toInt if x % i == 0) {
                if (primeParent(i) == -1) primeParent(i) = num
                else union(primeParent(i), num)
                while (x % i == 0) x /= i
            }
            if (x > 1) { // x is a prime number greater than sqrt(num)
                if (primeParent(x) == -1) primeParent(x) = num
                else union(primeParent(x), num)
            }
        }

        val groups = nums.map(find).toSet
        groups.size == 1 || (groups.size == 2 && groups.contains(find(1)))
    }
}
