public class Solution {
    public int LongestValidSubstring(string word, IList<string> forbidden) {
        int n = word.Length;
        int[] dp = new int[n + 1];
        int maxLen = 0;

        for (int i = 0; i < n; i++) {
            dp[i + 1] = dp[i] + 1; // Assume the current character extends the valid substring
            foreach (string f in forbidden) {
                if (i + 1 >= f.Length && word.Substring(i + 1 - f.Length, f.Length) == f) {
                    dp[i + 1] = 0; // Reset if a forbidden substring ends at this position
                    break;
                }
            }
            for (int j = i + 1 - dp[i + 1]; j <= i; j++) {
                bool isValid = true;
                foreach (string f in forbidden) {
                    if (j + f.Length <= i + 1 && word.Substring(j, f.Length) == f) {
                        isValid = false;
                        break;
                    }
                }
                if (!isValid) {
                    dp[i + 1] = i - j;
                    break;
                }
            }
            maxLen = Math.Max(maxLen, dp[i + 1]);
        }

        return maxLen;
    }
}
