#include <string.h>

int longestValidSubstring(char * word, char ** forbidden, int forbiddenSize) {
    int wordLen = strlen(word);
    int maxLen = 0;

    for (int start = 0; start < wordLen; ++start) {
        for (int end = start; end < wordLen; ++end) {
            int len = end - start + 1;
            int isValid = 1;

            for (int f = 0; f < forbiddenSize; ++f) {
                if (strstr(word + start, forbidden[f]) == word + start && strlen(forbidden[f]) <= len) {
                    isValid = 0;
                    break;
                }
            }

            if (isValid) {
                int currentLen = end - start + 1;
                if (currentLen > maxLen) {
                    maxLen = currentLen;
                }
            }
        }
    }

    return maxLen;
}
