class Solution {
  int longestValidSubstring(String word, List<String> forbidden) {
    int start = 0, maxLen = 0;
    Map<String, List<int>> forbiddenIndexes = {};

    for (var forbid in forbidden) {
      forbiddenIndexes[forbid] = [];
      int pos = word.indexOf(forbid);
      while (pos != -1) {
        forbiddenIndexes[forbid]!.add(pos);
        pos = word.indexOf(forbid, pos + 1);
      }
    }

    List<int> breaks = [];
    for (var indexes in forbiddenIndexes.values) {
      breaks.addAll(indexes);
    }
    breaks.sort();

    if (breaks.isEmpty) return word.length;

    for (int i = 0; i < breaks.length; i++) {
      int end = breaks[i];
      for (var forbid in forbidden) {
        if (end + forbid.length <= word.length && word.substring(end, end + forbid.length) == forbid) {
          maxLen = max(maxLen, end - start);
          start = end + forbid.length;
          break;
        }
      }
    }

    return max(maxLen, word.length - start);
  }

  int max(int a, int b) => a > b ? a : b;
}
