import java.util.List;

class Solution {
    public int longestValidSubstring(String word, List<String> forbidden) {
        int n = word.length();
        int maxLen = 0;
        int start = 0;

        while (start < n) {
            boolean invalidFound = false;
            int end = n;

            while (start < end) {
                String sub = word.substring(start, end);
                for (String forb : forbidden) {
                    if (sub.contains(forb)) {
                        end -= forb.length();
                        invalidFound = true;
                        break;
                    }
                }
                if (!invalidFound) {
                    maxLen = Math.max(maxLen, sub.length());
                    break;
                }
                invalidFound = false;
            }
            start++;
        }
        return maxLen;
    }
}
