import java.util.List;

class Solution {
    public int longestValidSubstring(String word, List<String> forbidden) {
        int maxLen = 0;
        int[] dp = new int[word.length() + 1];

        for (int i = 1; i <= word.length(); i++) {
            dp[i] = dp[i - 1] + 1;
            for (String forb : forbidden) {
                if (i >= forb.length() && word.substring(i - forb.length(), i).equals(forb)) {
                    dp[i] = 0;
                }
            }
            maxLen = Math.max(maxLen, dp[i]);
        }

        // Calculate maximum length considering splits
        int tempMax = 0;
        for (int i = 1; i <= word.length(); i++) {
            tempMax = Math.max(tempMax, dp[i]);
            if (dp[i] == 0) tempMax = 0; // Reset on invalid substring
            maxLen = Math.max(maxLen, tempMax);
        }

        return maxLen;
    }
}
