class Solution {

    /**
     * @param String $word
     * @param String[] $forbidden
     * @return Integer
     */
    function longestValidSubstring($word, $forbidden) {
        $n = strlen($word);
        $maxLength = 0;

        // Preprocess forbidden strings to create a trie
        $trie = [];
        foreach ($forbidden as $f) {
            $node = &$trie;
            for ($i = 0; $i < strlen($f); $i++) {
                if (!isset($node[$f[$i]])) {
                    $node[$f[$i]] = [];
                }
                $node = &$node[$f[$i]];
            }
            $node['end'] = true; // Mark the end of a forbidden string
        }

        for ($i = 0; $i < $n; $i++) {
            $node = $trie;
            for ($j = $i; $j < $n; $j++) {
                if (isset($node[$word[$j]])) {
                    $node = $node[$word[$j]];
                    if (isset($node['end'])) {
                        // Found a forbidden substring, break out of the loop
                        break;
                    }
                } else {
                    // No more matching in trie, safe to continue
                    $maxLength = max($maxLength, $j - $i + 1);
                    break;
                }
                if ($j == $n - 1) {
                    // Reached the end of $word without finding a forbidden substring
                    $maxLength = max($maxLength, $j - $i + 1);
                }
            }
        }

        return $maxLength;
    }
}
