class Solution {

    /**
     * @param String $word
     * @param String[] $forbidden
     * @return Integer
     */
    function longestValidSubstring($word, $forbidden) {
        $n = strlen($word);
        $maxLength = 0;
        $forbiddenSet = array_flip($forbidden); // Use flip to make checks faster

        for ($i = 0; $i < $n; $i++) {
            for ($j = $i; $j < $n; $j++) {
                $substr = substr($word, $i, $j - $i + 1);
                $foundForbidden = false;
                foreach ($forbiddenSet as $f => $val) {
                    if (strpos($substr, $f) !== false) {
                        $foundForbidden = true;
                        break;
                    }
                }
                if (!$foundForbidden) {
                    $maxLength = max($maxLength, strlen($substr));
                }
            }
        }

        // Optimized approach
        $validStart = 0;
        $currentLength = 0;
        for ($i = 0; $i < $n; ++$i) {
            $currentLength++;
            foreach ($forbidden as $f) {
                if (strlen($f) > $currentLength) continue;
                $endPos = $i - strlen($f) + 1;
                if (substr($word, $endPos, strlen($f)) == $f) {
                    $validStart = $endPos + 1;
                    $currentLength = $i - $validStart + 1;
                    break;
                }
            }
            $maxLength = max($maxLength, $currentLength);
        }

        return $maxLength;
    }
}
