class Solution(object):
    def longestValidSubstring(self, word, forbidden):
        """
        :type word: str
        :type forbidden: List[str]
        :rtype: int
        """
        max_length = 0
        dp = [0] * (len(word) + 1)  # dp[i] stores the length of the longest valid substring ending at index i-1

        for i in range(1, len(word) + 1):
            valid = True
            for f in forbidden:
                if i >= len(f) and word[i-len(f):i] == f:
                    valid = False
                    break
            if valid:
                dp[i] = dp[i-1] + 1
                max_length = max(max_length, dp[i])
            else:
                dp[i] = 0

        return max_length
