public class Solution {
    public long GetMaxFunctionValue(IList<int> receiver, long k) {
        int n = receiver.Count;
        long[] dp = new long[n];
        int[] nextIndex = new int[n];
        bool[] visited = new bool[n];
        long maxVal = 0;

        for (int i = 0; i < n; i++) {
            long sum = 0;
            int current = i;
            long passes = k;
            while (passes > 0) {
                if (visited[current]) {
                    long cycleLen = dp[current];
                    long cycles = passes / cycleLen;
                    sum += cycles * dp[nextIndex[current]];
                    passes %= cycleLen;
                }

                if (passes > 0) {
                    visited[current] = true;
                    sum += current;
                    nextIndex[current] = receiver[current];
                    dp[current]++;
                    current = receiver[current];
                    passes--;
                }
            }
            maxVal = Math.Max(maxVal, sum);

            // Reset visited for next iteration
            current = i;
            while (visited[current]) {
                visited[current] = false;
                current = receiver[current];
            }
        }

        return maxVal;
    }
}
