public class Solution {
    public long GetMaxFunctionValue(IList<int> receiver, long k) {
        int n = receiver.Count;
        long max = 0;
        for (int i = 0; i < n; i++) {
            int curr = i;
            long sum = curr;
            var visited = new Dictionary<int, int>();
            visited[curr] = 0;
            int steps = 1;
            while (steps <= k) {
                curr = receiver[curr];
                sum += curr;
                if (visited.ContainsKey(curr)) {
                    int cycleLen = steps - visited[curr];
                    long remainingCycles = (k - steps) / cycleLen;
                    long cycleSum = (sum - visited.Keys.Select(x => (long)x).Sum()) * remainingCycles;
                    sum += cycleSum;
                    break;
                } else {
                    visited[curr] = steps;
                }
                steps++;
            }
            max = Math.Max(max, sum);
        }
        return max;
    }
}
