#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    long long getMaxFunctionValue(vector<int>& receiver, long long k) {
        int n = receiver.size();
        unordered_map<int, long long> cache;
        long long maxVal = 0;

        for (int i = 0; i < n; ++i) {
            if (cache.find(i) != cache.end()) {
                maxVal = max(maxVal, cache[i]);
                continue;
            }

            int current = i;
            long long sum = i;
            vector<int> path = {i};
            unordered_map<int, int> visited;
            visited[i] = 0;

            for (long long j = 1; j <= k; ++j) {
                current = receiver[current];
                if (visited.count(current)) {
                    long long cycleLen = j - visited[current];
                    long long cycleSum = sum - path[visited[current]];
                    long long fullCycles = (k - j) / cycleLen;
                    sum += cycleSum * fullCycles;
                    j += cycleLen * fullCycles;

                    while (j < k) {
                        sum += current;
                        j++;
                        current = receiver[current];
                    }
                    break;
                } else {
                    sum += current;
                    path.push_back(current);
                    visited[current] = j;
                }
            }
            cache[i] = sum;
            maxVal = max(maxVal, sum);
        }
        return maxVal;
    }
};
