class Solution {
  int getMaxFunctionValue(List<int> receiver, int k) {
    int n = receiver.length;
    List<int> next = List<int>.filled(n, 0);
    List<int> count = List<int>.filled(n, 0);
    for (int i = 0; i < n; i++) {
      next[i] = receiver[i];
      count[i] = 1;
    }

    for (int p = 1; p <= k; p <<= 1) {
      if ((k & p) != 0) {
        List<int> newCount = List<int>.filled(n, 0);
        for (int i = 0; i < n; i++) {
          count[i] += count[next[i]];
          newCount[i] = count[i];
        }
        count = newCount;
      }
      List<int> newNext = List<int>.filled(n, 0);
      for (int i = 0; i < n; i++) {
        newNext[i] = next[next[i]];
      }
      next = newNext;
    }

    int maxVal = 0;
    for (int i = 0; i < n; i++) {
      maxVal = max(maxVal, count[i]);
    }
    return maxVal;
  }

  int max(int a, int b) => a > b ? a : b;
}
