func getMaxFunctionValue(receiver []int, k int64) int64 {
    n := len(receiver)
    visited := make([]bool, n)
    maxVal := int64(0)

    for i := 0; i < n; i++ {
        if visited[i] {
            continue
        }

        // Detect cycle and its length
        cycle := make([]int, 0)
        cycleIndices := make(map[int]int)
        current := i
        for !visited[current] {
            visited[current] = true
            cycle = append(cycle, current)
            cycleIndices[current] = len(cycle) - 1
            current = receiver[current]
        }

        // If current is part of the cycle detected
        if idx, exists := cycleIndices[current]; exists {
            cycleLen := len(cycle) - idx
            cycleSum := int64(0)
            for j := idx; j < len(cycle); j++ {
                cycleSum += int64(cycle[j])
            }

            preCycleSum := int64(0)
            for j := 0; j < idx; j++ {
                preCycleSum += int64(cycle[j])
            }

            // Calculate maximum value considering cycles
            for startIdx := idx; startIdx < len(cycle); startIdx++ {
                sum := preCycleSum + cycleSum*int64(k)/int64(cycleLen)
                if k%int64(cycleLen) != 0 {
                    for j := 0; j < int(k%int64(cycleLen)); j++ {
                        sum += int64(cycle[(startIdx+j)%cycleLen])
                    }
                }
                if sum > maxVal {
                    maxVal = sum
                }
            }
        }
    }

    return maxVal
}
