import java.util.HashMap;
import java.util.List;
import java.util.Map;

class Solution {
    public long getMaxFunctionValue(List<Integer> receiver, long k) {
        int n = receiver.size();
        long maxVal = 0;
        Map<Integer, Integer> next = new HashMap<>();
        Map<Integer, Long> sumCache = new HashMap<>();
        Map<Integer, Long> countCache = new HashMap<>();

        for (int i = 0; i < n; i++) {
            if (!next.containsKey(i)) {
                long sum = 0;
                long count = 0;
                int current = i;
                while (!next.containsKey(current)) {
                    next.put(current, receiver.get(current));
                    sum += current;
                    count++;
                    current = receiver.get(current);
                    if (count > k) break;
                }
                long cycleSum = sum;
                long cycleCount = count;

                current = receiver.get(current);
                while (count <= k && current != i) {
                    cycleSum += current;
                    cycleCount++;
                    current = receiver.get(current);
                }

                for (Map.Entry<Integer, Integer> entry : next.entrySet()) {
                    int start = entry.getKey();
                    current = start;
                    sum = 0;
                    count = 0;
                    while (count < k) {
                        if (countCache.containsKey(current) && (k - count) >= countCache.get(current)) {
                            long cycleTimes = (k - count) / cycleCount;
                            sum += cycleSum * cycleTimes;
                            count += cycleCount * cycleTimes;
                        } else {
                            sum += current;
                            count++;
                            current = next.get(current);
                        }
                    }
                    maxVal = Math.max(maxVal, sum);
                    sumCache.put(start, sum);
                    countCache.put(start, count);
                }
                next.clear();
            }
        }
        return maxVal;
    }
}
