/**
 * @param {number[]} receiver
 * @param {number} k
 * @return {number}
 */
var getMaxFunctionValue = function(receiver, k) {
    const n = receiver.length;
    let maxVal = 0;

    // Precompute sums and lengths to handle cycles efficiently
    let visited = new Array(n).fill(false);
    let sums = new Array(n).fill(0);
    let lengths = new Array(n).fill(0);

    for (let i = 0; i < n; ++i) {
        if (!visited[i]) {
            let current = i;
            let cycleSum = 0;
            let cycleLength = 0;
            while (!visited[current]) {
                visited[current] = true;
                cycleSum += current;
                cycleLength++;
                current = receiver[current];
            }

            // Apply precomputed values for all nodes in the cycle
            current = i;
            while (lengths[current] === 0) {
                sums[current] = cycleSum;
                lengths[current] = cycleLength;
                current = receiver[current];
            }
        }
    }

    for (let start = 0; start < n; ++start) {
        let sum = 0;
        let current = start;
        for (let pass = 0; pass < k; ++pass) {
            sum += current;
            if (pass + lengths[current] <= k) {
                sum += (k - pass) / lengths[current] * sums[current];
                break;
            }
            current = receiver[current];
        }
        maxVal = Math.max(maxVal, sum);
    }

    return maxVal;
};
