class Solution {
    fun getMaxFunctionValue(receiver: List<Int>, k: Long): Long {
        val n = receiver.size
        val next = Array(n) { arrayOf(-1L, 0L) }
        for (i in receiver.indices) {
            next[i][0] = receiver[i].toLong()
            next[i][1] = i.toLong()
        }

        var maxSteps = 1
        while (1L shl maxSteps <= k) maxSteps++

        val dp = Array(n) { Array(maxSteps + 1) { arrayOf(-1L, 0L) } }
        for (i in 0 until n) dp[i][0] = next[i]

        for (j in 1..maxSteps) {
            for (i in 0 until n) {
                val prev = dp[i][j - 1][0].toInt()
                dp[i][j][0] = dp[prev][j - 1][0]
                dp[i][j][1] = dp[i][j - 1][1] + dp[prev][j - 1][1]
            }
        }

        var ans = 0L
        for (i in 0 until n) {
            var sum = 0L
            var x = i
            var kLeft = k
            for (j in maxSteps downTo 0) {
                if (kLeft >= (1L shl j)) {
                    sum += dp[x][j][1] + (1L shl j)
                    x = dp[x][j][0].toInt()
                    kLeft -= (1L shl j)
                }
            }
            ans = maxOf(ans, sum)
        }
        return ans
    }
}
