class Solution {
    fun getMaxFunctionValue(receiver: List<Int>, k: Long): Long {
        var maxVal = 0L
        val n = receiver.size
        val visitCount = LongArray(n)

        for (i in 0 until n) {
            var current = i
            var total = i.toLong()
            var passes = k

            while (passes > 0) {
                current = receiver[current]
                total += current
                passes--

                // Increment visit count for cycle detection
                visitCount[current]++
                if (visitCount[current] > 1) {
                    // Found a cycle, calculate the cycle's contribution in one complete iteration
                    val cycleLength = visitCount[current]
                    val cyclePasses = passes / cycleLength
                    val cycleContribution = (cycleLength * (cycleLength + 1)) / 2
                    total += cycleContribution * cyclePasses
                    passes %= cycleLength // Remaining passes after completing the cycles
                    break
                }
            }
            maxVal = maxOf(maxVal, total)

            // Reset visit count for the next starting player
            visitCount.fill(0)
        }

        return maxVal
    }
}
