class Solution {

    /**
     * @param Integer[] $receiver
     * @param Integer $k
     * @return Integer
     */
    function getMaxFunctionValue($receiver, $k) {
        $n = count($receiver);
        $dp = array_fill(0, $n, 0);
        $maxValue = PHP_INT_MIN;

        for ($i = 0; $i < $n; $i++) {
            if ($dp[$i] == 0) {
                $current = $i;
                $cycle = [];
                while (!isset($visited[$current])) {
                    $visited[$current] = true;
                    $cycle[] = $current;
                    $current = $receiver[$current];
                }
                $cycleSize = count($cycle);
                $sum = array_sum($cycle);
                for ($j = 0; $j < $cycleSize; $j++) {
                    $totalPasses = $k;
                    $value = 0;
                    if ($totalPasses >= $cycleSize) {
                        $completeCycles = intdiv($totalPasses, $cycleSize);
                        $value += $sum * $completeCycles;
                        $totalPasses -= $completeCycles * $cycleSize;
                    }
                    for ($pass = 0; $pass < $totalPasses; $pass++) {
                        $value += $cycle[($j + $pass) % $cycleSize];
                    }
                    $dp[$cycle[$j]] = $value + $cycle[$j]; // Add self id to the total
                    $maxValue = max($maxValue, $dp[$cycle[$j]]);
                }
            } else {
                $maxValue = max($maxValue, $dp[$i]);
            }
        }
        return $maxValue;
    }
}
