class Solution {

    /**
     * @param Integer[] $receiver
     * @param Integer $k
     * @return Integer
     */
    function getMaxFunctionValue($receiver, $k) {
        $n = count($receiver);
        $maxValue = 0;
        $visit = array_fill(0, $n, -1);

        for ($i = 0; $i < $n; ++$i) {
            $current = $i;
            $steps = 0;
            $sum = 0;
            while ($visit[$current] < $i) {
                $visit[$current] = $i;
                $sum += $current;
                $current = $receiver[$current];
                ++$steps;
                if ($steps == $k) break;
            }
            if ($steps < $k && $visit[$current] == $i) { // Detected a cycle
                $cycleLength = $steps;
                $cycleSum = $current;
                $temp = $receiver[$current];
                while ($temp != $current) {
                    $cycleSum += $temp;
                    $temp = $receiver[$temp];
                    ++$cycleLength;
                }
                $sum += ($k - $steps) / $cycleLength * $cycleSum;
                $remaining = ($k - $steps) % $cycleLength;
                $temp = $current;
                while ($remaining-- > 0) {
                    $sum += $temp;
                    $temp = $receiver[$temp];
                }
            }
            $maxValue = max($maxValue, $sum);
        }
        return $maxValue;
    }
}
