function getMaxFunctionValue(receiver: number[], k: number): number {
  let maxValue = 0;
  const n = receiver.length;
  const visit = new Array(n).fill(0);
  const sum = new Array(n).fill(0);
  let cycleSum = 0, cycleLength = 0, steps = 0;

  for (let i = 0; i < n; i++) {
    let x = i;
    cycleSum = 0;
    cycleLength = 0;
    steps = k;

    while (visit[x] !== i + 1) {
      if (steps === 0) break;
      visit[x] = i + 1;
      cycleSum += x;
      x = receiver[x];
      cycleLength++;
      steps--;
    }

    let totalSum = cycleSum;
    if (steps > 0) {
      let remainingSteps = steps % cycleLength;
      cycleSum = (steps / cycleLength >> 0) * cycleSum;
      totalSum += cycleSum;
      x = i;
      while (remainingSteps-- > 0) {
        x = receiver[x];
        totalSum += x;
      }
    }
    maxValue = Math.max(maxValue, totalSum);
  }
  return maxValue;
}
