class Solution {
    fun maximumSum(nums: List<Int>): Long {
        fun primeFactors(n: Int): Map<Int, Int> {
            var number = n
            val factors = mutableMapOf<Int, Int>()
            var count = 0
            while (number % 2 == 0) {
                count++
                number /= 2
            }
            if (count > 0) factors[2] = count % 2
            var i = 3
            while (i <= Math.sqrt(number.toDouble()).toInt()) {
                count = 0
                while (number % i == 0) {
                    count++
                    number /= i
                }
                if (count > 0) factors[i] = count % 2
                i += 2
            }
            if (number > 2) factors[number] = 1
            return factors
        }

        val map = mutableMapOf<Map<Int, Int>, Long>()
        for (num in nums) {
            val factors = primeFactors(num)
            val sum = map.getOrDefault(factors, 0L) + num
            map[factors] = sum
        }

        return map.values.maxOrNull() ?: 0L
    }
}
