class Solution {
    fun maximumSum(nums: List<Int>): Long {
        fun gcd(a: Int, b: Int): Int = if (b == 0) a else gcd(b, a % b)
        fun lcm(a: Int, b: Int): Int = a / gcd(a, b) * b

        val freq = mutableMapOf<Int, Long>()
        for (num in nums) {
            var product = 1
            for (i in 2..Math.sqrt(num.toDouble()).toInt()) {
                var count = 0
                var n = num
                while (n % i == 0) {
                    n /= i
                    count++
                }
                if (count % 2 == 1) product *= i
            }
            if (num / product > 1) product *= num / product
            freq[product] = freq.getOrDefault(product, 0) + num
        }

        return freq.values.maxOrNull() ?: 0
    }
}
