class Solution {
    fun maximumSum(nums: List<Int>): Long {
        fun primeFactors(n: Int): Map<Int, Int> {
            var num = n
            val factors = mutableMapOf<Int, Int>()
            for (i in 2..Math.sqrt(num.toDouble()).toInt()) {
                while (num % i == 0) {
                    factors[i] = factors.getOrDefault(i, 0) + 1
                    num /= i
                }
            }
            if (num > 1) factors[num] = factors.getOrDefault(num, 0) + 1
            return factors.filter { it.value % 2 != 0 }
        }

        val maskToSum = mutableMapOf<Int, Long>()
        nums.forEach { num ->
            val factors = primeFactors(num)
            var mask = 0
            factors.keys.forEach { prime ->
                val bit = prime % 31 // Use primes modulo 31 for a bitmask. Primes are sparse enough for this approach.
                mask = mask or (1 shl bit)
            }
            maskToSum[mask] = maskToSum.getOrDefault(mask, 0) + num
        }

        return maskToSum.values.maxOrNull() ?: 0L
    }
}
