object Solution {
    def maximumSum(nums: List[Int]): Long = {
        def primeFactors(n: Int): Map[Int, Int] = {
            var factors = Map[Int, Int]()
            var number = n
            var i = 2
            while (i * i <= number) {
                while (number % i == 0) {
                    factors = factors.updated(i, factors.getOrElse(i, 0) + 1)
                    number /= i
                }
                i += 1
            }
            if (number > 1) factors.updated(number, factors.getOrElse(number, 0) + 1)
            else factors
        }

        def isSquareFree(factors: Map[Int, Int]): Boolean = {
            factors.forall { case (_, power) => power % 2 == 0 }
        }

        val factorSignatures = nums.map(num => primeFactors(num).filter(_._2 % 2 != 0))

        val groups = factorSignatures.zipWithIndex.groupBy(_._1).view.mapValues(_.map(_._2)).toMap

        val maxSum = groups.values.map(indices => indices.map(nums(_)).sum).max

        maxSum
    }
}
