object Solution {
    def maximumSum(nums: List[Int]): Long = {
        def primeFactors(n: Int): Map[Int, Int] = {
            var number = n
            var factors = Map.empty[Int, Int]
            var i = 2
            while (i * i <= number) {
                while (number % i == 0) {
                    factors = factors.updated(i, factors.getOrElse(i, 0) + 1)
                    number /= i
                }
                i += 1
            }
            if (number > 1) factors.updated(number, factors.getOrElse(number, 0) + 1) else factors
        }

        def normalizeFactors(factors: Map[Int, Int]): Map[Int, Int] = 
            factors.filter(_._2 % 2 != 0)

        val normalizedFactors = nums.map(n => normalizeFactors(primeFactors(n)))
        val factorGroups = normalizedFactors.distinct.map(f => (f, normalizedFactors.zip(nums).filter(_._1 == f).map(_._2.toLong).sum))
        factorGroups.maxBy(_._2)._2
    }
}
