object Solution {
    def maximumSum(nums: List[Int]): Long = {
        def gcd(a: Int, b: Int): Int = if (b == 0) a else gcd(b, a % b)
        def lcm(a: Int, b: Int): Int = a / gcd(a, b) * b

        def factorize(n: Int): Map[Int, Int] = {
            var factors = Map.empty[Int, Int]
            var num = n
            var i = 2
            while (i * i <= num) {
                while (num % i == 0) {
                    factors = factors.updated(i, factors.getOrElse(i, 0) + 1)
                    num /= i
                }
                i += 1
            }
            if (num > 1) factors.updated(num, factors.getOrElse(num, 0) + 1) else factors
        }

        def isPerfectSquare(factors: Map[Int, Int]): Boolean = factors.forall(_._2 % 2 == 0)

        val factorizedNums = nums.map(n => factorize(n).filter(_._2 % 2 != 0))
        val signatureToIndexSum = scala.collection.mutable.Map[Map[Int, Int], Long]().withDefaultValue(0L)

        for ((factors, i) <- factorizedNums.zip(nums)) {
            signatureToIndexSum(factors) += i
        }

        signatureToIndexSum.values.max
    }
}
