using System;
using System.Linq;

public class Solution {
    public long MaxSpending(int[][] values) {
        int m = values.Length, n = values[0].Length;
        int[] indexes = new int[m]; // Track the current index for each shop
        long total = 0;
        int day = 1;

        // Flatten all values and sort them in non-increasing order
        var allValues = values.SelectMany((value, index) => value.Select((item) => new { Value = item, Shop = index })).ToList();
        allValues.Sort((a, b) => b.Value.CompareTo(a.Value));

        foreach (var item in allValues) {
            // Calculate the price based on the day and add to the total
            total += (long)item.Value * day++;
        }

        return total;
    }
}
