#include <vector>
#include <queue>
#include <functional>

using namespace std;

class Solution {
public:
    long long maxSpending(vector<vector<int>>& values) {
        auto cmp = [](const pair<int, int>& a, const pair<int, int>& b) {
            return a.first < b.first || (a.first == b.first && a.second > b.second);
        };
        priority_queue<pair<int, int>, vector<pair<int, int>>, decltype(cmp)> pq(cmp);
        long long total = 0;
        int days = 1, m = values.size(), n = values[0].size();

        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                pq.push({values[i][j], j});
            }
        }

        while (!pq.empty()) {
            int value = pq.top().first;
            pq.pop();
            total += (long long)value * days;
            days++;
        }

        return total;
    }
};
