#include <stdlib.h>

long long maxSpending(int** values, int valuesSize, int* valuesColSize) {
    int totalItems = valuesSize * valuesColSize[0];
    int* allValues = (int*)malloc(sizeof(int) * totalItems);
    int index = 0;
    for (int i = 0; i < valuesSize; i++) {
        for (int j = 0; j < valuesColSize[i]; j++) {
            allValues[index++] = values[i][j];
        }
    }

    // Sort the array in non-increasing order
    for (int i = 0; i < totalItems - 1; i++) {
        for (int j = i + 1; j < totalItems; j++) {
            if (allValues[i] < allValues[j]) {
                int temp = allValues[i];
                allValues[i] = allValues[j];
                allValues[j] = temp;
            }
        }
    }

    long long maxSpend = 0;
    for (int i = 0; i < totalItems; i++) {
        maxSpend += (long long)allValues[i] * (i + 1);
    }

    free(allValues);
    return maxSpend;
}
