#include <stdio.h>
#include <stdlib.h>

int cmp(const void *a, const void *b) {
    int arg1 = *(const int *)a;
    int arg2 = *(const int *)b;
    return (arg2 - arg1);
}

long long maxSpending(int** values, int valuesSize, int* valuesColSize) {
    int totalItems = 0;
    for (int i = 0; i < valuesSize; i++) {
        totalItems += valuesColSize[i];
    }
    int* sortedValues = (int*)malloc(sizeof(int) * totalItems);
    int index = 0;
    for (int i = 0; i < valuesSize; i++) {
        for (int j = 0; j < valuesColSize[i]; j++) {
            sortedValues[index++] = values[i][j];
        }
    }

    qsort(sortedValues, totalItems, sizeof(int), cmp);

    long long maxSpend = 0;
    for (int i = 0; i < totalItems; i++) {
        maxSpend += (long long)sortedValues[i] * (i + 1);
    }

    free(sortedValues);
    return maxSpend;
}
